INSERT INTO `books` VALUES ('75', '破碎的四月(金装版)', '《破碎的四月》是阿尔巴尼亚当代最著名的作家和诗人伊斯梅尔·卡达莱的代表作之一。最早出版于1978年。小说讲述了阿尔巴尼亚高原地区的残酷复仇故事，极力讽刺和批判了荒谬的卡努法典。2001年曾被巴西导演沃尔特·塞勒斯改编成电影《太阳背后》。', '5000', '阿尔巴尼亚', '89.40', '65463', '4', 'true', 'true', '1', '17', '9');
