INSERT INTO `books` VALUES ('102', '倾城之恋(旧版)', '《倾城之恋》是一个动听而又近人情的故事。《倾城之恋》里，从腐旧的家庭里走出来的白流苏，香港之战的洗礼并不曾将她感化成为革命女性；香港之战影响范柳原，使他转向平实的生活，终于结婚了，但结婚并不使他变为圣人，完全放弃往日的生活习惯与作风。', '5000', '张爱玲', '56.40', '7497', '5', 'false', 'true', '1', '16', '14');
INSERT INTO `books` VALUES ('103', '第二性(旧版)', '《第二性》是法国思想家西蒙娜·波伏娃创作的社会学著作。作者以涵盖哲学、历史、文学、生物学、古代神话和风俗的文化内容为背景，纵论了从原始社会到现代社会的历史演变中，妇女的处境、地位和权利的实际情况，探讨了女性个体发展史所显示的性别差异。此书被...', '5000', '西蒙娜·波伏娃', '33.50', '16746', '5', 'false', 'true', '1', '32', '15');
INSERT INTO `books` VALUES ('104', '简·爱(旧版)', '简爱一般指简·爱。 《简·爱》（Jane Eyre）是英国女作家夏洛蒂·勃朗特创作的长篇小说，是一部具有自传色彩的作品。该小说讲述孤女简·爱自幼父母双亡，寄养于舅母家，备受虐待，后被舅母打发到洛伍德义塾去。洛伍德义塾环境恶劣，但她顽强地活了...', '5000', '夏洛蒂·勃朗特', '45.00', '16567', '5', 'false', 'true', '1', '33', '16');
INSERT INTO `books` VALUES ('129', '假如给我三天光明(旧版)', '《假如给我三天光明》是美国当代作家海伦·凯勒的散文代表作。该文的前半部分主要写了海伦变成盲聋人后的生活，后半部分则介绍了海伦的求学生涯。同时也介绍她体会不同的丰富多彩的生活以及她的慈善活动等等。她以一个身残志坚的柔弱女子的视角，告诫身体健全...', '5000', '海伦凯勒', '55.20', '1654', '5', 'false', 'true', '1', '10', '17');
INSERT INTO `books` VALUES ('130', '安妮日记(旧版)', '《安妮日记》是德籍犹太人安妮·弗兰克写的日记，是其藏身密室时的生活和情感的记载。作为一名成长中的少女，她在日记中吐露了与母亲不断发生冲突的困惑以及对性的好奇。同时，对于藏匿且充满恐怖的25个月的密室生活的记录，也使这本《安妮日记》成为德军占领...', '5000', '安妮·弗兰克', '63.40', '7497', '5', 'false', 'true', '1', '18', '18');
INSERT INTO `books` VALUES ('131', '我们仨(旧版)', '《我们仨》是当代作家杨绛创作的散文集，首次出版于2003年7月。该书讲述了一个单纯温馨的家庭几十年平淡无奇、相守相助、相聚相失的经历。作者杨绛以简洁而沉重的语言，回忆了先后离她而去的女儿钱瑗、丈夫钱锺书，以及一家三口那些快乐而艰难、爱与痛的日子。', '5000', '杨绛', '78.30', '5646', '5', 'false', 'true', '1', '28', '19');
INSERT INTO `books` VALUES ('132', '厨房(旧版)', '该书以厨房是一个女人的出发点和停泊地讲述了一个厨房的故事。一切的一切都已经寂灭，惟有心灵不可言说。厨房是一个女人的出发点和停泊地。作品目录 作者简介 徐坤，女，1965年3月出生于沈阳，现居北京。中国社会科学院文学研究所副...', '5000', '徐坤', '59.80', '65463', '5', 'false', 'true', '1', '34', '20');
INSERT INTO `books` VALUES ('133', '草叶集(旧版)', '《草叶集》是美国诗人沃尔特·惠特曼创作的诗集，首次出版于1855年。《草叶集》是浪漫主义诗集，共收有诗歌300余首。诗歌奔腾壮阔，大气飞扬，汪洋恣肆，豪放不羁；使用朴实粗犷的语言，创造出独具一格的自由体，近于口语，节奏鲜明。《草叶集》是美国诗人惠...', '5000', '惠特曼', '45.30', '5647', '5', 'false', 'true', '1', '11', '21');
INSERT INTO `books` VALUES ('134', '舒婷诗集(旧版)', '舒婷是朦胧诗艺术成就的杰出代表。本书共四辑，收录了她124首诗，包括《致橡树》、《神女峰》、《祖国，我亲爱的祖国》等脍炙人口的名篇。她的诗擅长综合运用通感、比喻、象征、联想等艺术手法来表达内心感受，既有女性诗歌所特有的细腻、浪漫、柔美，又兼具...', '5000', '舒婷', '35.80', '16746', '5', 'false', 'true', '1', '17', '22');
INSERT INTO `books` VALUES ('135', '平凡的世界(盗版)', '《平凡的世界》是中国作家路遥创作的一部全景式地表现中国当代城乡社会生活的百万字长篇小说。全书共三部。1986年12月首次出版。该书以中国70年代中期到80年代中期十年间为背景，通过复杂的矛盾纠葛，以孙少安和孙少平两兄弟为中心，刻画了当时社会各阶层众多...', '5000', '路遥', '12.50', '15664', '6', 'false', 'true', '1', '24', '1');
INSERT INTO `books` VALUES ('136', '穆斯林葬礼(盗版)', '穆斯林葬礼是一本浓缩了中国视角和穆斯林风俗的特点的书', '5000', '霍达', '132.50', '19875', '6', 'false', 'true', '1', '25', '2');
INSERT INTO `books` VALUES ('137', '挪威的森林(盗版)', '《挪威的森林》是日本作家村上春树于1987年所著的一部长篇爱情小说。故事讲述主角纠缠在情绪不稳定且患有精神疾病的直子和开朗活泼的小林绿子之间，展开了自我成长的旅程。自该书在日本问世，截止2012年在日本共销出1500余万册。', '18500', '村上春树', '36.50', '213', '6', 'false', 'true', '1', '27', '3');
INSERT INTO `books` VALUES ('138', '基督山伯爵(盗版)', '《基督山伯爵》是法国作家大仲马创作的长篇小说，首次发表于1844—1846年。故事讲述19世纪法国皇帝拿破仑“百日王朝”时期，法老号大副爱德蒙·唐泰斯受船长委托，为拿破仑党人送了一封信，遭到两个卑鄙小人和法官的陷害，被打入黑牢。期间狱友法利亚神甫向他...', '5000', '大仲马', '78.50', '16746', '6', 'false', 'false', '1', '11', '4');
INSERT INTO `books` VALUES ('139', '教父(盗版)', '《教父》是美国作家马里奥·普佐创作的长篇小说，1969年首次出版。该小说主要描写了1945年至1955年之间，意大利移民科利奥尼家族在困境中的挣扎和生存之道，科利奥尼家族是五大黑帮势力集团之一，为了能够生存不惜进行各种残忍的攻略，用枪、用斧头、用刑具，...', '250', '马里奥・普佐', '45.30', '16567', '6', 'false', 'false', '1', '35', '5');
INSERT INTO `books` VALUES ('140', '苏菲的世界(盗版)', '《苏菲的世界》是挪威作家乔斯坦·贾德创作的一本关于西方哲学史的长篇小说，它以小说的形式，通过一名哲学导师向一个叫苏菲的女孩传授哲学知识的经过，揭示了西方哲学史发展的历程。《苏菲的世界》被誉为20世纪百部经典著作之一。《苏菲的世界》1991年首次以...', '5000', '乔斯坦贾德', '35.40', '1654', '6', 'false', 'true', '1', '21', '6');
INSERT INTO `books` VALUES ('141', '麦田里的守望者(盗版)', '《麦田里的守望者》是美国作家杰罗姆·大卫·塞林格创作的唯一一部长篇小说，首次出版于1951年。塞林格将故事的起止局限于16岁的中学生霍尔顿·考尔菲德从离开学校到纽约游荡的三天时间内，并借鉴了意识流天马行空的写作方法，充分探索了一个十几岁少年的内心...', '5000', '杰罗姆·大卫·塞林格', '22.60', '7497', '6', 'false', 'true', '1', '23', '7');
INSERT INTO `books` VALUES ('142', '白鹿原(盗版)', '《白鹿原》是作家陈忠实创作的长篇小说，由陈忠实历时六年创作完成，首次出版于1993年。该小说以陕西关中地区白鹿原上白鹿村为缩影，通过讲述白姓和鹿姓两大家族祖孙三代的恩怨纷争，表现了从清朝末年到二十世纪七八十年代长达半个多世纪的历史变化。1998年，...', '5000', '陈忠实', '19.10', '5646', '6', 'false', 'true', '1', '30', '8');
INSERT INTO `books` VALUES ('143', '破碎的四月(盗版)', '《破碎的四月》是阿尔巴尼亚当代最著名的作家和诗人伊斯梅尔·卡达莱的代表作之一。最早出版于1978年。小说讲述了阿尔巴尼亚高原地区的残酷复仇故事，极力讽刺和批判了荒谬的卡努法典。2001年曾被巴西导演沃尔特·塞勒斯改编成电影《太阳背后》。', '5000', '阿尔巴尼亚', '89.40', '65463', '6', 'false', 'true', '1', '17', '9');
INSERT INTO `books` VALUES ('144', '万历十五年(盗版)', '《万历十五年》是黄仁宇创作的历史学著作，首次刊行于1981年。《万历十五年》从“大历史观”的研究视角出发，选取了明朝万历十五年（1587年）作为考察切入点，运用历史小说的叙事模式和传记体式的章节，通过对关键历史人物悲惨命运的描述，探析了晚明帝国走向...', '1200', '黄仁宇', '53.40', '5647', '6', 'false', 'true', '1', '18', '10');
